import React, { useState } from "react";

const initialCalcData = {
    'current-savings': 10000,
    'yearly-contribution': 1200,
    'expected-return': 7,
    'duration': 10
}

const CalcForm = (props) => {

    const [userInput, setUserInput] = useState(initialCalcData);

    const resetHandler = () => {
        setUserInput
    }

    const submitHandler = (e) => {
        e.preventDefault();
        const calcData = {
            currentSavings: enteredCurrentSavings,    
            yearlySavings: enteredYearlySavings,    
            interest: enteredInterest,    
            duration: enteredDuration,    
        };

        props.onSaveCalcData(calcData);

        setEnteredCurrentSavings('');
        setEnteredYearlySavings('');
        setEnteredInterest('');
        setEnteredDuration('');
    }

    const inputChangeHandler = (input, value) => {
        setUserInput((prevInput) => {
            return {
                ...prevInput,
                [input]: value,
            };
        })
    }

    return(
        <form className="form" onSubmit={submitHandler}>
            <div className="input-group">
                <p>
                    <label htmlFor="current-savings">Current Savings ($)</label>
                    <input type="number" id="current-savings" onChange={(e) => inputChangeHandler('current-savings', e.target.value)} />
                </p>
                <p>
                    <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                    <input type="number" id="yearly-contribution" onChange={(e) => inputChangeHandler('yearly-contribution', e.target.value)} />
                </p>
            </div>
            <div className="input-group">
                <p>
                    <label htmlFor="expected-return">
                    Expected Interest (%, per year)
                    </label>
                    <input type="number" id="expected-return" onChange={(e) => inputChangeHandler('expected-return', e.target.value)} />
                </p>
                <p>
                    <label htmlFor="duration">Investment Duration (years)</label>
                    <input type="number" id="duration" onChange={(e) => inputChangeHandler('duration', e.target.value)} />
                </p>
            </div>
            <p className="actions">
            <button onClick={resetHandler} type="reset" className="buttonAlt">
                Reset
            </button>
            <button type="submit" className="button">
                Calculate
            </button>
            </p>
        </form>
    );
}

export default CalcForm;
